import * as Font from 'expo-font';
import AppLoading from 'expo-app-loading';
import { StatusBar } from 'expo-status-bar';
import React, { Component } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, Alertxs } from 'react-native';

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      color: 'orange',
      fontsLoaded: false,
    };
    this.pressedOnce = this.pressedOnce.bind(this);
  }

  componentDidMount() {

  }

  async loadFonts() {
    const fonts = await Font.loadAsync({
      'Ubuntu-Regular': {
        uri: require('./assets/ubuntuFont/Ubuntu-Regular.ttf'),
        display: Font.FontDisplay.FALLBACK,
      },
    });
    return Promise.all([fonts]);
  }

  pressedOnce() {
    if (this.state.color != 'orange') {
      this.setState({ color: 'orange' });
    } else {
      this.setState({ color: '#6b78c9' });
    }
  }

  render() {

    if (this.state.fontsLoaded) {
      const styles = StyleSheet.create({
        container: {
          flex: 1,
          backgroundColor: '#fff',
          alignItems: 'center',
          justifyContent: 'center',
        },
        button: {
          backgroundColor: this.state.color,
          height: 40,
          width: 120,
          justifyContent: 'center',
          alignItems: 'center',
          borderRadius: 10,
          shadowColor: '#000',
          shadowOffset: {
            width: 0,
            height: 4,
          },
          shadowOpacity: 0.32,
          shadowRadius: 5.46,
          elevation: 9,
        },
        myFont: {
          color: 'white',
          fontFamily: 'Ubuntu-Regular'
        }
      });

      return (
        <View style={styles.container} >
          <Text style={styles.myFont}>Open up App.js to start working on your app!</Text>
          <Text>{'\n'}</Text>
          <TouchableOpacity style={styles.button} onPress={this.pressedOnce}>
            <Text style={styles.myFont}>
              Touch Me
            </Text>
          </TouchableOpacity>
          <StatusBar style="auto" />
        </View>
      );
    } else {
      return <AppLoading startAsync={this.loadFonts} onFinish={() => { this.setState({ fontsLoaded: true }) }} onError={console.warn} />;
    }
  }

}

export default App;
